INSERT INTO `reviews` VALUES (49, 'FTP：File Transfer Protocol，文件传输协议。是用于在网络上进行文件传输的一套标准协议，使用客户 服务器模式，使用 TCP 数据报，提供交互式访问，双向传输。  ', 63, 'YY', '2022-03-03 21:45:42', 1, 2);
INSERT INTO `reviews` VALUES (50, 'TFTP（Trivial File Transfer Protocol，简单文件传输协议）一个小且易实现的文件传输协议，也使用客户 服务器方式，使用 UDP 数据报，只支持文件传输而不支持交互，没有列目录，不能对用户进行身份鉴定。  ', 63, 'YY', '2022-03-03 21:49:10', 1, 2);
INSERT INTO `reviews` VALUES (51, 'SMTP（Simple Mail Transfer Protocol，简单邮件传输协议）是在 Internet 传输 Email 的标准，是一个相对简单的基于文本的协议。在其之上指定了一条消息的一个或多个接收者（在大多数情况下被确认是存在的），然后消息文本会被传输。可以很简单地通过 Telnet 程序来测试一个 SMTP 服务器。SMTP 使用 TCP 端口 25。  ', 63, 'YY', '2022-03-03 21:49:45', 0, 2);
INSERT INTO `reviews` VALUES (52, 'DHCP（Dynamic Host Configuration Protocol，动态主机设置协议）是一个局域网的网络协议，使用 UDP 协议工作，主要有两个用途：用于内部网络或网络服务供应商自动分配 IP 地址给用户 用于内部网络管理员作为对所有电脑作中央管理的手段', 63, 'YY', '2022-03-03 21:52:06', 0, 2);
INSERT INTO `reviews` VALUES (53, 'SNMP（Simple Network Management Protocol，简单网络管理协议）构成了互联网工程工作小组（IETF，Internet Engineering Task Force）定义的 Internet 协议族的一部分。该协议能够支持网络管理系统，用以监测连接到网络上的设备是否有任何引起管理上关注的情况。', 63, 'YY', '2022-03-03 21:52:29', 0, 2);
