DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`  (
  `user_id` varchar(12) CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NOT NULL COMMENT '用户id',
  `username` varchar(12) CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NOT NULL COMMENT '用户名(昵称)',
  `password` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NOT NULL COMMENT '密码',
  `avatar` mediumtext CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NULL COMMENT '头像url，最大16MB',
  `email` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NULL DEFAULT NULL COMMENT '邮箱',
  `description` varchar(30) CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NULL DEFAULT NULL COMMENT '用户描述',
  `background` mediumtext CHARACTER SET utf8 COLLATE utf8_unicode_520_ci NULL COMMENT '用户主页背景图',
  PRIMARY KEY (`user_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_unicode_520_ci ROW_FORMAT = Dynamic;

